import './App.css';
import Particles from "react-particles";
import { loadFull } from "tsparticles";
import { useCallback } from "react";
import Header from './MyComponents/Header';
import Banner from './MyComponents/Banner';
import About from './MyComponents/About';
import Skills from './MyComponents/Skills';
import Education from './MyComponents/Education';
import Projects from './MyComponents/Projects';
import Footer from './MyComponents/Footer';
import 'bootstrap/dist/css/bootstrap.min.css';



function App() {
  const options = {
    //...
    particles: {
      number: {
        value: 80,
        density: {
          enable: true,
          area: 800
        }
      },
      color: {
        value: ["#2EB67D", "#ECB22E", "#E01E5B", "#36C5F0"]
      },
      shape: {
        type: "circle"
      },
      opacity: {
        value: 1
      },
      size: {
        value: { min: 1, max: 8 }
      },
      links: {
        enable: true,
        distance: 150,
        color: "#808080",
        opacity: 0.4,
        width: 1
      },
      move: {
        enable: true,
        speed: 5,
        direction: "none",
        random: false,
        straight: false,
        outModes: "out"
      }
    },
    interactivity: {
      events: {
        onHover: {
          enable: true,
          mode: "grab"
        },
        onClick: {
          enable: true,
          mode: "push"
        }
      },
      modes: {
        grab: {
          distance: 140,
          links: {
            opacity: 1
          }
        },
        push: {
          quantity: 4
        }
      }
    }
  };

  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine);
  }, []);
  return (
    <div style={{ backgroundColor: "black" }}>
      <section className="container-fluid back " id="Home" style={{  backgroundColor: "#FFC8D8" }}>
        <Particles options={options} init={particlesInit} />
        <Header></Header>
        <Banner></Banner>
      </section>
      <section className="container-fluid back" id="About" style={{ backgroundColor: "black" }}>
        <About></About>
      </section>
      <section className="container-fluid back" id="Skills" style={{ backgroundColor: "black" }}>
        <Skills></Skills>
      </section>
      <section className="container-fluid back" id="Education" style={{ backgroundColor: "black" }}>
        <Education></Education>
      </section>
      <section className="container-fluid back" id="Projects" style={{ backgroundColor: "black" }}>
        <Projects></Projects>
      </section>
      <section className="container-fluid back" id="Footer">
        <Footer></Footer>
      </section>
    </div>
  );
}

export default App;
